transforms.code:
# Get amount of seconds for the tag history chart.

units = value['units']
time = value['time']

if units == 0:
	if time > 1209600:
		time = 1209600
	return time
elif units == 1:
	if time > 20160:
		time = 20160
	return time * 60
elif units == 2:
	if time > 336:
		time = 336
	return time * 3600
else:
	if time > 14:
		time = 14
	return time * 86400

if 'NONE' in value:
	return '--success'
elif 'HEAT' in value:
	return '--error'
elif 'COOL' in value:
	return '--qual-2'

if value:
	if '&' in self.view.params.area:
		return 'Departments'
	else:
		return 'Department'
else:
	return 'Area'

if value == 0:
	return 1209600
elif value == 1:
	return 20160
elif value == 2:
	return 336
else:
	return 14

onChange.script:
time = self.getSibling("Time").props.value
units = currentValue.value

if units == 0:
	if time > 1209600:
		self.getSibling("Time").props.value = 1209600
elif units == 1:
	if time > 20160:
		self.getSibling("Time").props.value = 20160
elif units == 2:
	if time > 336:
		self.getSibling("Time").props.value = 336
else:
	if time > 14:
		self.getSibling("Time").props.value = 14

config.script:
if self.custom.toolTipShown == False:
	self.requestTooltip()
	self.custom.toolTipShown = True
else:
	self.removeTooltip()
	self.custom.toolTipShown = False
