# image. If useBuiltIn is false and image bytes are null, then do not
# display this image component.

if value['useBuiltIn'] == 1:
	return False
else:
	if value['image'] == None:
		return False
	else:
		return True

# Only display built-in icon if useBuiltIn is set to true.

if bool(value['useBuiltIn']) == False:
	return False
else:
	return True

# Display 'Person' or the built-in icon path (could also be 'Person')
# if useBuiltIn is true. Display 'No picture uploaded' or the file name
# of the image if useBuiltIn is false.

if value['useBuiltIn'] == 1:
	if value['builtIn'] == None:
		return 'Person'
	else:
		return value['builtIn']
else:
	if value['imageStr'] == None:
		return 'No picture uploaded'
	else:
		return value['imageStr']

# If useBuiltIn is equal to 1, then make the current tab 'builtIn'; 
# otherwise make the current tab 'fromComputer'. Then, return the
# userConfig that was retrieved from the db.

if value['usebuiltin'] == 1:
	self.getChild("Add Photo").custom.currTab = 'builtIn'
else:
	self.getChild("Add Photo").custom.currTab = 'fromComputer'

return ({'image':value['profileimage'],'imageStr':value['imagestr'],'builtIn':value['builtin'],
