onChange.script:
config = currentValue.value

if config['mode'].value == 'historical':
	if previousValue != None:
		if previousValue.value != None:
			startDate = config['startDate'].value
			endDate = config['endDate'].value		

			daysBetween = system.date.daysBetween(startDate,endDate)

			if daysBetween > 14:
				oldStartDate = previousValue.value['startDate'].value
				oldEndDate = previousValue.value['endDate'].value

				if oldStartDate == startDate:
					self.props.config.startDate = system.date.addDays(endDate,-14)
				else:
					self.props.config.endDate = system.date.addDays(startDate,14)
elif config['mode'].value == 'realtime':
	units = config['measureOfTime'].value
	time = config['unitOfTime'].value

	if units == 'seconds':
		if time > 1209600:
			self.props.config.unitOfTime = 1209600
	elif units == 'minutes':
		if time > 20160:
			self.props.config.unitOfTime = 20160
	elif units == 'hours':
		if time > 336:
			self.props.config.unitOfTime = 336
	elif units == 'days':
		if time > 14:
			self.props.config.unitOfTime = 14
	elif units == 'weeks':
		if time > 2:
			self.props.config.unitOfTime = 2
	else:
		self.props.config.unitOfTime = 2
		self.props.config.measureOfTime = 'weeks'

transforms.code:
# If no pens are enabled, then show this label (informing the user to select a pen).

for pen in value:
	if pen.enabled == True:
		return False
return True

